year_state_records.map:
function(doc) {
    if (doc.year && doc.state && doc.record_chunk) {
        emit([doc.year, doc.state], doc.record_chunk.length);
    }
}

total_loaned_per_state.map:
function (doc) {
  if(doc.year && doc.state && Array.isArray(doc.record_chunk)) {
    for (var i = 0; i < doc.record_chunk.length; i++) {
      var record = doc.record_chunk[i];
      var loan_amount = parseFloat(record.loan_amount);
      if (!isNaN(loan_amount)) {
        emit([doc.year, doc.state], loan_amount);
      }
    }
  }
}

average_interest_rate_per_state.reduce:
function (keys, values, rereduce) {
  var total = 0;
  var totalCount = 0;

  if (rereduce) {
    for (var i = 0; i < values.length; i++) {
      total += values[i];
      totalCount++;
    }
  } else {
    total = sum(values);
    totalCount = values.length;
  }

  var average = totalCount > 0 ? total / totalCount : 0;
  return average;
}

